                  , care_time
                  , child_no
                  , teacher_no
                  , start_time
                  , end_time
                  , status
                  , contract
                  , contract_time
                  , created_dt
                  , created_by
                  , update_dt
                  , update_by
                  , contents
                  , name
) values (
                    #{user_no}
                  , #{to_name}
                  , #{to_addr}
                  , #{to_tel}
                  , #{care_time}
                  , #{child_no}
                  , #{teacher_no}
                  , #{start_time}
                  , #{end_time}
                  , #{status}
                  , #{contract}
                  , #{contract_time}
                  , #{created_dt}
                  , #{created_by}
                  , #{update_dt}
                  , #{update_by}
                  , #{contents}
                  , #{name}
)

mapper.select:
SELECT COUNT(*)
FROM
    ilinkcare.comment
WHERE
    order_no = #{orderNo}
AND
    teacher_no = #{teacherNo}
AND
    user_no = #{userNo}

